import React, { useReducer, useCallback } from "react";
import { Container, Grid, Card, List, Icon, Button } from "semantic-ui-react";
import { FileInput } from "components/FileInput";

interface IAction<P> {
  type: string;
  payload: P;
}

const ADD_FILES = "ADD_FILES";

const filesReducer = (state: any[] = [], action: IAction<any[]>): any[] => {
  switch (action.type) {
    case ADD_FILES:
      return [...state, ...action.payload];
    default:
      return state;
  }
};

const addFiles = (files: any[]) => ({ type: ADD_FILES, payload: files });

function App() {
  const [state, dispatch] = useReducer(filesReducer, []);
  const handleAddFiles = useCallback(
    (files: any[]) => dispatch(addFiles(files)),
    [dispatch]
  );
  return (
    <Container>
      <Grid>
        <Grid.Row>
          <Card fluid>
            <Card.Content>
              <List divided relaxed>
                {state.map((file) => (
                  <List.Item>
                    <List.Icon
                      name="reddit"
                      size="large"
                      verticalAlign="middle"
                    />
                    <List.Content>
                      <List.Header>{file.name}</List.Header>
                      <List.Description>{file.size}</List.Description>
                    </List.Content>
                    <List.Icon verticalAlign="middle">
                      <Button.Group basic>
                        <Button icon>
                          <Icon name="settings" />
                        </Button>
                        <Button icon>
                          <Icon name="trash alternate outline" />
                        </Button>
                      </Button.Group>
                    </List.Icon>
                  </List.Item>
                ))}
              </List>
            </Card.Content>
          </Card>
        </Grid.Row>
        <Grid.Row>
          <FileInput name="files" onAddFiles={handleAddFiles} />
        </Grid.Row>
      </Grid>
    </Container>
  );
}

export default App;
