import React from "react";
import { Grid, Container } from "semantic-ui-react";
import { FileInput } from "components/FileInput";

interface IAction<P> {
  type: string;
  payload: P;
}

const ADD_FILES = "ADD_FILES";

const filesReducer = (state: any[] = [], action: IAction<any[]>): any[] => {
  switch (action.type) {
    case ADD_FILES:
      return [...state, ...action.payload];
    default:
      return state;
  }
};

function App() {
  return (
    <Container>
      <Grid>
        <Grid.Row>
          <FileInput name="files" />
        </Grid.Row>
      </Grid>
    </Container>
  );
}

export default App;
