import React, { Fragment } from "react";
import { Button } from "semantic-ui-react";

interface IProps {
  name: string;
}

export const FileInput: React.FC<IProps> = ({ name }) => {
  return (
    <Fragment>
      <input type="file" name={name} hidden />
      <Button>Add file</Button>
    </Fragment>
  );
};
