import React, { useReducer, useCallback } from "react";
import { Grid, Container, List, Icon } from "semantic-ui-react";
import { FileInput } from "components/FileInput";

interface IAction<P> {
  type: string;
  payload: P;
}

const ADD_FILES = "ADD_FILES";

const filesReducer = (state: any[] = [], action: IAction<any[]>): any[] => {
  switch (action.type) {
    case ADD_FILES:
      return [...state, ...action.payload];
    default:
      return state;
  }
};

const addFiles = (files: any[]) => ({ type: ADD_FILES, payload: files });

function App() {
  const [state, dispatch] = useReducer(filesReducer, []);
  const handleAddFiles = useCallback(
    (files: any[]) => dispatch(addFiles(files)),
    [dispatch]
  );
  return (
    <Container>
      <Grid>
        <Grid.Row>
          <List divided>
            {state.map((file) => (
              <List.Item key={file.name}>
                <List.Content>
                  <List.Header>{file.name}</List.Header>
                  <List.Icon name="ellipsis horizontal"></List.Icon>
                </List.Content>
              </List.Item>
            ))}
          </List>
        </Grid.Row>
        <Grid.Row>
          <FileInput name="files" onAddFiles={handleAddFiles} />
        </Grid.Row>
      </Grid>
    </Container>
  );
}

export default App;
